CREATE TABLE app_address(id INTEGER GENERATED BY DEFAULT AS IDENTITY(START WITH 1) PRIMARY KEY, firstName varchar(255), lastName varchar(255), fullName varchar(255), street1 varchar(255), zip varchar(255), city varchar(255), state varchar(255), country varchar(255));
CREATE TABLE app_user(uuid varchar(255), email varchar(255), firstName varchar(255), lastName varchar(255), userLanguage varchar(255), openId varchar(255), idAddress integer);